import React, { useState, useEffect } from 'react';
import takimService from '../services/takimService';
import './PuanTablosu.css';

function PuanTablosu() {
  const [takimlar, setTakimlar] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Veriler yükleniyor...');
        const data = await takimService.getPuanDurumu();
        console.log('Yüklenen veriler:', data);
        setTakimlar(data);
        setLoading(false);
      } catch (err) {
        console.error('Hata detayı:', err);
        setError('Puan durumu yüklenirken bir hata oluştu: ' + err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Yükleniyor...</div>;
  if (error) return <div style={{color: 'red', padding: '20px'}}>{error}</div>;

  return (
    <div className="puan-tablosu">
      <h2>PREMIER LİG PUAN DURUMU</h2>
      <table>
        <thead>
          <tr>
            <th></th>
            <th>TAKIM</th>
            <th>O</th>
            <th>G</th>
            <th>B</th>
            <th>M</th>
            <th>AG</th>
            <th>YG</th>
            <th>AV</th>
            <th>P</th>
          </tr>
        </thead>
        <tbody>
          {takimlar.map((takim, index) => (
            <tr key={index}>
              <td>
                <img src={takim.team_logo} alt={takim.team_name} width="20" />
              </td>
              <td>{takim.team_name}</td>
              <td>{takim.played}</td>
              <td>{takim.wins}</td>
              <td>{takim.draws}</td>
              <td>{takim.losses}</td>
              <td>{takim.goals_for}</td>
              <td>{takim.goals_against}</td>
              <td>{takim.goal_diff}</td>
              <td className="puan">{takim.points}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PuanTablosu;
