import axios from 'axios';

const API_URL = 'http://localhost:5026/api';

const takimService = {
  // Premier Lig puan durumunu getir
  getPuanDurumu: async () => {
    try {
      console.log('API isteği yapılıyor...');
      const response = await axios.get(`${API_URL}/takimlar`);
      console.log('API yanıtı:', response.data);
      return response.data;
    } catch (error) {
      console.error('Puan durumu getirilirken hata detayı:', error.response || error);
      throw error;
    }
  }
};

export default takimService;
