import axios from 'axios';

const API_URL = '/api';

const takimService = {
  // Tüm takımları getir (puan durumu)
  getPuanDurumu: async () => {
    try {
      const response = await axios.get(`${API_URL}/takimlar`);
      return response.data;
    } catch (error) {
      console.error('Puan durumu getirilirken hata:', error);
      throw error;
    }
  },

  // Yeni takım ekle
  addTakim: async (takim) => {
    try {
      const response = await axios.post(`${API_URL}/takimlar`, takim);
      return response.data;
    } catch (error) {
      console.error('Takım eklenirken hata:', error);
      throw error;
    }
  },

  // Takım güncelle
  updateTakim: async (id, takim) => {
    try {
      const response = await axios.patch(`${API_URL}/takimlar/${id}`, takim);
      return response.data;
    } catch (error) {
      console.error('Takım güncellenirken hata:', error);
      throw error;
    }
  }
};

export default takimService;
