import React, { useState, useEffect } from 'react';
import takimService from '../services/takimService';
import './PuanTablosu.css';

function PuanTablosu() {
  const [takimlar, setTakimlar] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Veriler yükleniyor...'); // Debug için log
        const data = await takimService.getPuanDurumu();
        console.log('Yüklenen veriler:', data); // Debug için log
        setTakimlar(data);
        setLoading(false);
      } catch (err) {
        console.error('Hata detayı:', err); // Hata detayını göster
        setError('Puan durumu yüklenirken bir hata oluştu: ' + err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Yükleniyor...</div>;
  if (error) return <div style={{color: 'red', padding: '20px'}}>{error}</div>;

  return (
    <div className="puan-tablosu">
      <h2>PREMIER LİG PUAN DURUMU</h2>
      <table>
        <thead>
          <tr>
            <th>SIRA</th>
            <th>TAKIM</th>
            <th>O</th>
            <th>G</th>
            <th>B</th>
            <th>M</th>
            <th>A</th>
            <th>Y</th>
            <th>AV</th>
            <th>P</th>
          </tr>
        </thead>
        <tbody>
          {takimlar && takimlar.length > 0 ? (
            takimlar.map((takim) => (
              <tr key={takim._id}>
                <td>{takim.sira}</td>
                <td>{takim.ad}</td>
                <td>{takim.oynananMac}</td>
                <td>{takim.galibiyet}</td>
                <td>{takim.beraberlik}</td>
                <td>{takim.maglubiyet}</td>
                <td>{takim.attigiGol}</td>
                <td>{takim.yedigiGol}</td>
                <td>{takim.averaj}</td>
                <td className="puan">{takim.puan}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="10">Veri bulunamadı</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default PuanTablosu;
